import React, { useEffect, useState } from 'react';

const UserCard = ({ user }) => {
    const [posts, setPosts] = useState([]);

    useEffect(() => {
      fetch(`https://jsonplaceholder.typicode.com/posts?userId=${user.id}`)
        .then(response => response.json())
        .then(data => setPosts(data));
    }, [user.id]);

    return (
      <div className="user-card" >
        <div className="user-name" style={{
          display: 'flex',
          flexDirection: 'row',
          alignItems: 'center',
        }}>
          <h2 style={{
            fontSize: '16px',
            margin: '0',
          }}>{user.name}</h2>
          <p style={{
            fontSize: '14px',
            margin: '0',
            marginLeft: '10px',
          }}>{posts.length}</p>
        </div>
        <a href={`/users/${user.id}`} className="user-link" style={{
          display: 'flex',
          flexDirection: 'row',
          alignItems: 'center',
        }}>
          <img src={user.avatar} alt={user.name} avatar style={{
            width: '50px',
            height: '50px',
            borderRadius: '50%',
            marginRight: '10px',
          }} />
          <p style={{
            fontSize: '14px',
            margin: '0',
          }}>View Profile</p>
        </a>
      </div>
    );
  };

export default UserCard;
