import React, { useState, useEffect } from 'react';
import axios from 'axios';

function UserDetails({ user, posts }) {
  const [currentTime, setCurrentTime] = useState(null);
  const [clockInterval, setClockInterval] = useState(null);
  const [isClockRunning, setIsClockRunning] = useState(true);

  useEffect(() => {
    // Fetch the current time for the user's country
    const fetchCurrentTime = () => {
      axios.get(`http://worldtimeapi.org/api/timezone/${user.address.city}`)
        .then(response => {
          setCurrentTime(response.data.datetime);
        })
        .catch(error => {
          console.error('Error fetching current time: ', error);
        });
    };

    fetchCurrentTime();

    // Set up an interval to update the clock every second
    const intervalId = setInterval(() => {
      if (isClockRunning) {
        fetchCurrentTime();
      }
    }, 1000);

    setClockInterval(intervalId);

    return () => {
      clearInterval(intervalId);
    };
  }, [user.address.city, isClockRunning]);

  const handleClockToggle = () => {
    setIsClockRunning(!isClockRunning);
  };

  return (
    <div>
      <h2>{user.name}</h2>
      <p>Username: {user.username}</p>
      <p>Catchphrase: {user.company.catchPhrase}</p>

      <div className="clock">
        <h3>Current Time</h3>
        <p>Country: {user.address.city}</p>
        <div className="clock-display">
          {currentTime && <p>{currentTime}</p>}
          <button onClick={handleClockToggle}>
            {isClockRunning ? 'Pause' : 'Start'}
          </button>
        </div>
      </div>

      <div className="user-address">
        <h3>Address</h3>
        <p>{user.address.street}, {user.address.suite}</p>
        <p>{user.address.city}, {user.address.zipcode}</p>
      </div>

      <p>Email: {user.email}</p>
      <p>Phone: {user.phone}</p>

      <button>Back to User Directory</button>

      <h3>Posts</h3>
      <div className="user-posts">
        {posts.map(post => (
          <div key={post.id} className="user-post">
            <h4>{post.title}</h4>
            <p>{post.body}</p>
          </div>
        ))}
      </div>
    </div>
  );
}

export default UserDetails;
