import React, { useState, useEffect } from 'react';
import CountryClockSelector from './CountryClockSelector';
import UserPosts from './UserPosts';

const UserProfilePage = ({ match }) => {
  const [user, setUser] = useState({});
  const [posts, setPosts] = useState([]);
  const [clock, setClock] = useState(new Date());

  useEffect(() => {
    fetch(`https://jsonplaceholder.typicode.com/users/${match.params.id}`)
      .then(response => response.json())
      .then(data => setUser(data));

    fetch(`https://jsonplaceholder.typicode.com/posts?userId=${match.params.id}`)
      .then(response => response.json())
      .then(data => setPosts(data));

    // Start the clock
    const interval = setInterval(() => {
      setClock(new Date());
    }, 1000);

    return () => clearInterval(interval);
  }, [match.params.id]);

  const handleCountryChange = (event) => {
    // Get the selected country
    const country = event.target.value;

    // Update the clock
    fetch(`http://worldtimeapi.org/api/timezone/${country}`)
      .then(response => response.json())
      .then(data => {
        const time = data.datetime;
        const date = new Date(time);
        setClock(date);
      });
  };

  const handlePauseStartClick = () => {
    // Pause or start the clock
    const isPaused = clock.isPaused;
    clock.isPaused = !isPaused;
  };

  return (
    <div className="user-profile">
      <h1>User Profile</h1>

      <CountryClockSelector
        clock={clock}
        onCountryChange={handleCountryChange}
        onPauseStartClick={handlePauseStartClick}
      />

      <div className="user-details">
        <h2>{user.name}</h2>
        <p>{user.username}</p>
        <p>{user.catchPhrase}</p>

        <h3>Contact</h3>
        <p>{user.address}</p>
        <p>{user.email}</p>
        <p>{user.phone}</p>
      </div>

      <UserPosts posts={posts} />

      <button className="back-button">Back</button>
    </div>
  );
};

export default UserProfilePage;
