import React, { useEffect, useState } from "react";

const UserCard = ({ user }) => {
  const [posts, setPosts] = useState([]);

  useEffect(() => {
    fetch(`https://jsonplaceholder.typicode.com/posts?userId=${user.id}`)
      .then((response) => response.json())
      .then((data) => setPosts(data));
  }, [user.id]);

  return (
    <div
      className="user-card"
      style={{
        width: "100%",
        margin: "5px",
        marginRight: "20px",
        paddingLeft: "20px",
        border: "1px solid #ccc",
        borderRadius: "5px",
        boxShadow: "0px 0px 5px rgba(0, 0, 0, 0.25)",
        backgroundColor:"skyblue"
      }}
    >
      <div
        className="user-name"
        style={{ display: "flex", flexDirection: "row", alignItems: "center", justifyContent:"space-between", paddingTop:"1%",paddingBottom:"1%"}}
      >
        <div>
          <h2 style={{ fontSize: "16px", margin: "0" }}>Name: {user.name}</h2>
        </div>
        <div className="post" style={{marginRight:"2%"}}> 
          <h2 style={{ fontSize: "16px", margin: "0" }}>
            Post: {posts.length}
          </h2>
        </div>
      </div>
      {/* <a
        href={`/users/${user.id}`}
        className="user-link"
        style={{ display: "flex", flexDirection: "row", alignItems: "center" }}
      >
        <p style={{ fontSize: "14px", margin: "0" }}>View Profile</p>
      </a> */}
    </div>
  );
};

export default UserCard;
