import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Switch, Route, Link, useParams, useHistory } from 'react-router-dom';
import axios from 'axios';
import UserDetails from './component/UserDetails';

function UserCard({ user }) {
  return (
    <Link to={`/profile/${user.id}`}>
      <div className="user-card">
        <p>{user.name}</p>
        <p>Posts: {user.posts ? user.posts.length : 0}</p>
      </div>
    </Link>
  );
}


function UserProfile() {
  const { id } = useParams();
  const history = useHistory();
  const [user, setUser] = useState({ posts: [] });

  useEffect(() => {
    // Fetch user details and posts based on the user ID.
    axios.get(`https://jsonplaceholder.typicode.com/users/${id}`)
      .then(response => {
        setUser(response.data);
        return axios.get(`https://jsonplaceholder.typicode.com/posts?userId=${id}`);
      })
      .then(response => {
        setUser(prevUser => ({ ...prevUser, posts: response.data }));
      })
      .catch(error => {
        console.error("Error fetching user data", error);
      });
  }, [id]);

  const goBack = () => {
    history.goBack();
  }

  return (
    <div className="user-profile">
      <button onClick={goBack}>Back</button>
      <h1>{user.name}</h1>
      <p>Username: {user.username}</p>
      <p>Catchphrase: {user.company.catchPhrase}</p>
      <div className="user-address">
        <p>{user.address.street}, {user.address.city}</p>
        <p>{user.address.email}</p>
        <p>{user.phone}</p>
      </div>
      <h2>Posts:</h2>
      <div className="user-posts">
        {user.posts.map(post => (
          <div key={post.id} className="post-card">
            <h3>{post.title}</h3>
            <p>{post.body}</p>
          </div>
        ))}
      </div>
    </div>
  );
}

function App() {
  const [users, setUsers] = useState([]);
  const [posts, setPosts] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    // Fetch user data
    axios.get('https://jsonplaceholder.typicode.com/users')
      .then(response => {
        setUsers(response.data);
        setLoading(false);
      })
      .catch(error => {
        console.error('Error fetching user data: ', error);
        setLoading(false);
      });

    // Fetch post data
    axios.get('https://jsonplaceholder.typicode.com/posts')
      .then(response => {
        setPosts(response.data);
      })
      .catch(error => {
        console.error('Error fetching post data: ', error);
      });
  }, []);

  return (
    <Router>
      <div className="App">
        <Switch>
          <Route
            exact
            path="/"
            render={() => (
              <div>
                <h1>User Directory</h1>
                {users.map(user => (
                  <Link key={user.id} to={`/user/${user.id}`}>
                    <UserCard user={user} posts={posts.filter(post => post.userId === user.id)} />
                  </Link>
                ))}
              </div>
            )}
          />
          <Route
            path="/user/:id"
            render={({ match }) => {
              const userId = match.params.id;
              const user = users.find(user => user.id === parseInt(userId));
              if (user) {
                return <UserDetails user={user} posts={posts.filter(post => post.userId === user.id)} />;
              } else {
                return <div>User not found</div>;
              }
            }}
          />
        </Switch>
      </div>
    </Router>
  );
}

export default App;
