import React from "react";

// UserPosts component
const UserPosts = ({ posts }) => {
  // Function to return an array of 3 empty post cards
  const renderEmptyPostCards = () => {
    const emptyPostCards = [];
    for (let i = 0; i < 3; i++) {
      emptyPostCards.push(
        <div
          key={i}
          className={`post-card inline empty ${
            i === 2 ? "last-post" : ""
          }`}
          // style={{
          //  // display: "inline-block",
          //   width: "23%",
          //   height: "50%",
          //   margin: "10px",
          //   padding: "10px",
          //   border: "1px solid black",
          // }}
        />
      );
    }
    return emptyPostCards;
  };

  return (
    <div className="user-posts">
      <div className="border">
        {posts.map((post, index) => (
          <div
            key={post.id}
            className={`post-card inline ${
              index % 3 === 2 ? "last-post" : ""
            }`}
            style={{
            //  display: "inline-block",
              width: "23%",
              margin: "10px",
              padding: "10px",
              border: "1px solid black",
              height: "100%"
            }}
          >
            <h4>Post Title :</h4>
            <p style={{ fontSize: "16px", marginBottom: "5px" }}>
              {post.title}
            </p>
            <h4>Content :</h4>
            <p style={{ fontSize: "14px", marginBottom: "5px" }}>
              {post.body}
            </p>
          </div>
        ))}
        {posts.length < 3 && renderEmptyPostCards()}
      </div>
    </div>
  );
};

export default UserPosts;
