import React, { useState, useEffect } from 'react';
import UserCard from './UserCard';

const UserDirectoryPage = () => {
  const [users, setUsers] = useState([]);

  useEffect(() => {
    fetch('https://jsonplaceholder.typicode.com/users')
      .then(response => response.json())
      .then(data => setUsers(data));
  }, []);

  return (
    <div className="user-directory">
      <h1>User Directory</h1>
      <div className="user-cards" style={{ display: 'flex', flexWrap: 'wrap', justifyContent: 'space-between' }}>
        <div className="user-card-header" style={{ display: 'flex', flexDirection: 'row', alignItems: 'center', width: '100%', marginBottom: '10px' }}>
          <h3 style={{ fontSize: '16px', margin: '0' }}>Name</h3>
          <h3 style={{ fontSize: '16px', margin: '0' }}>Posts</h3>
        </div>
        {users.map(user => (
          <UserCard key={user.id} user={user} />
        ))}
      </div>
    </div>
  );
};

export default UserDirectoryPage;
