import React, { useEffect, useState } from "react";
import UserPosts from "./UserPost";

const UserProfilePage = ({ match }) => {
  const [posts, setPosts] = useState([]);
  const [address, setAddress] = useState(null);

  useEffect(() => {
    fetch("https://jsonplaceholder.typicode.com/posts")
      .then((response) => response.json())
      .then((data) => setPosts(data));

    fetch("https://jsonplaceholder.typicode.com/users/1/address")
      .then((response) => response.json())
      .then((data) => setAddress(data));
  }, []);

  const renderUserInfo = () => {
    return (
      <div className="user-info">
        <h1 style={{ fontSize: "24px", margin: 0 }}>User Profile</h1>
        <div className="address">
          <h2 style={{ fontSize: "18px", margin: 0 }}>Address</h2>
        </div>
      </div>
    );
  };

  const goBack = () => {
    window.location.href = "/"; // Replace with the URL you want to navigate back to
  };

  return (
    <div className="user-profile">
      <button
        onClick={goBack}
        style={{
          position: "absolute",
          top: "10px",
          left: "10px",
          background: "#000",
          color: "#fff",
          padding: "5px 10px",
          border: "none",
          borderRadius: "5px",
          cursor: "pointer",
        }}
      >
        Back
      </button>
      <div
        style={{
          display: "flex",
          flexDirection: "row",
          justifyContent: "space-between",
          width: "100%",
          margin: "10px",
          marginRight: "5px",
          paddingLeft: "20px",
          border: "1px solid #ccc",
          borderRadius: "5px",
          boxShadow: "0px 0px 5px rgba(0, 0, 0, 0.25)",
        }}
      >
        <div className="user-profile-left" style={{ flex: 1, padding: "10px" }}>
          {posts.length > 0 && address ? (
            <div>{renderUserInfo()}</div>
          ) : (
            <div>Loading...</div>
          )}
        </div>
        <div className="user-profile-right" style={{ flex: 1, padding: "10px" }}>
          {address ? (
            <div>
              {/* Render user address information here */}
            </div>
          ) : (
            <div>Loading Address...</div>
          )}
        </div>
      </div>
    </div>
  );
};

export default UserProfilePage;
