import React, { useState, useEffect } from 'react';

const CountryClockSelector = ({ clock, onCountryChange, onPauseStartClick }) => {
  const [countries, setCountries] = useState([]);
  const [currentTime, setCurrentTime] = useState({
    time: new Date(),
    isPaused: false,
  });

  useEffect(() => {
    // Start the clock
    if (!currentTime.isPaused) {
      const interval = setInterval(() => {
        setCurrentTime((prevTime) => ({
          ...prevTime,
          time: new Date(prevTime.time.getTime() + 1000),
        }));
      }, 1000);

      return () => clearInterval(interval);
    }
  }, [currentTime.isPaused]);

  useEffect(() => {
    fetch('http://worldtimeapi.org/api/timezone')
      .then((response) => response.json())
      .then((data) => setCountries(data));
  }, []);

  const handleCountryChange = (event) => {
    // Get the selected country
    const country = event.target.value;

    // Update the clock
    fetch(`http://worldtimeapi.org/api/timezone/${country}`)
      .then((response) => response.json())
      .then((data) => {
        const time = new Date(data.utc_datetime);
        setCurrentTime((prevTime) => ({
          ...prevTime,
          time,
        }));
      });
  };

  const handlePauseStartClick = () => {
    setCurrentTime((prevTime) => ({
      ...prevTime,
      isPaused: !prevTime.isPaused,
    }));
  };

  return (
    <div className="country-clock" style={{display:'flex',flexDirection:"row"}}>
      <div>
      <select id="country-selector" onChange={handleCountryChange}>
        {countries.map((country) => (
          <option key={country} value={country}>
            {country}
          </option>
        ))}
      </select>
      </div>
      <div id="clock">{currentTime.time.toLocaleTimeString()}</div>
      <button id="pause-start" onClick={handlePauseStartClick}>
        {currentTime.isPaused ? 'Start' : 'Pause'}
      </button>

    </div>
  );
};

export default CountryClockSelector;
