import React, { useState, useEffect } from 'react';
import { useParams, useHistory } from 'react-router-dom';
import axios from 'axios';

function UserCard({ user }) {
  return (
    <Link to={`/user/${user.id}`}>
      <div className="user-card">
        <p>{user.name}</p>
        <p>Posts: {user.posts.length}</p>
      </div>
    </Link>
  );
}

function UserProfile() {
  const { id } = useParams();
  const history = useHistory();
  const [user, setUser] = useState({
    posts: [],
    address: {},
    company: {},
  });
  const [currentTime, setCurrentTime] = useState('');
  const [isClockRunning, setIsClockRunning] = useState(true);

  useEffect(() => {
    // Fetch user details
    axios
      .get(`https://jsonplaceholder.typicode.com/users/${id}`)
      .then((response) => {
        setUser(response.data);
      })
      .catch((error) => {
        console.error('Error fetching user data', error);
      });

    // Fetch user's posts
    axios
      .get(`https://jsonplaceholder.typicode.com/posts?userId=${id}`)
      .then((response) => {
        setUser((prevUser) => ({ ...prevUser, posts: response.data }));
      })
      .catch((error) => {
        console.error('Error fetching user posts', error);
      });

    // Fetch current time
    const fetchCurrentTime = () => {
      axios
        .get('http://worldtimeapi.org/api/timezone/America/Argentina/Salta')
        .then((response) => {
          setCurrentTime(response.data.utc_datetime);
        })
        .catch((error) => {
          console.error('Error fetching current time', error);
        });
    };

    if (isClockRunning) {
      fetchCurrentTime();
      const clockInterval = setInterval(fetchCurrentTime, 1000);
      return () => clearInterval(clockInterval);
    }
  }, [id, isClockRunning]);

  const toggleClock = () => {
    setIsClockRunning((prevIsClockRunning) => !prevIsClockRunning);
  };

  return (
    <div className="user-profile">
      <button onClick={() => history.goBack()}>Back</button>
      <h1>{user.name}</h1>
      <p>Username: {user.username}</p>
      <p>Catchphrase: {user.company.catchPhrase}</p>
      <div className="user-address">
        <p>
          {user.address.street}, {user.address.city}
        </p>
        <p>{user.email}</p>
        <p>{user.phone}</p>
      </div>
      <div className="user-clock">
        <h2>Current Time</h2>
        <p>{currentTime}</p>
        <button onClick={toggleClock}>
          {isClockRunning ? 'Pause Clock' : 'Start Clock'}
        </button>
      </div>
      <h2>Posts:</h2>
      <div className="user-posts">
        {user.posts.map((post) => (
          <div key={post.id} className="post-card">
            <h3>{post.title}</h3>
            <p>{post.body}</p>
          </div>
        ))}
      </div>
    </div>
  );
}

export default UserProfile;
