import React, { useEffect, useState } from "react";
import UserPosts from "./UserPost";
import { useMatch } from "react-router-dom";

const UserProfilePage = ({ user }) => {
  const [posts, setPosts] = useState([]);
  const [userInfo, setUserInfo] = useState(null);

  const match = useMatch("/users/:id");
  const { id } = match?.params;

  useEffect(() => {
    // Fetch user-specific data
    fetch(`https://jsonplaceholder.typicode.com/users/${id}`)
      .then((response) => response.json())
      .then((data) => setUserInfo(data));

    // Fetch user's posts
    fetch(`https://jsonplaceholder.typicode.com/posts?userId=${id}`)
      .then((response) => response.json())
      .then((data) => setPosts(data));
  }, [id]);

  const goBack = () => {
    window.location.href = "/"; // Replace with the URL you want to navigate back to
  };

  return (
    <div>
      <button onClick={goBack} style={{ float: "left" }}>
        Back
      </button>
      <div>
        <h2 style={{ marginLeft: "40%" }}>Country Dropdown</h2>
      </div>
      <div>
        <h3 style={{ marginLeft: "45%" }}>Profile Page</h3>
      </div>
      {posts.length > 0 ? (
        <div>
          {userInfo && (
            <div className="user-profile">
              <div className="user-info" style={{ float: "left", width: "50%" }}>
                <h1 style={{ fontSize: "24px", margin: 0 }}>User Profile</h1>
                <p>Username: {userInfo.username}</p>
                <p>Name: {userInfo.name}</p>
                <p>Email: {userInfo.email}</p>
                <p>Phone: {userInfo.phone}</p>
              </div>
              {userInfo.address && (
                <div className="address" style={{ float: "right", width: "100%" }}>
                  <h2 style={{ fontSize: "18px", margin: 0 }}>Address</h2>
                  <p>Street: {userInfo.address.street}</p>
                  <p>Suite: {userInfo.address.suite}</p>
                  <p>City: {userInfo.address.city}</p>
                  <p>Zipcode: {userInfo.address.zipcode}</p>
                </div>
              )}
              <div style={{ clear: "both" }}></div>
            </div>
          )}
        </div>
      ) : (
        <div>Loading...</div>
      )}
    </div>
  );
};

export default UserProfilePage;
