const UserProfilePage = ({ match }) => {
  const [posts, setPosts] = useState([]);
  const [address, setAddress] = useState(null);

  // Fetch the posts from the API when the component is first mounted
  useEffect(() => {
    fetch("https://jsonplaceholder.typicode.com/posts")
      .then((response) => response.json())
      .then((data) => setPosts(data));

    // Fetch the user's address from the API
    fetch("https://jsonplaceholder.typicode.com/users/1/address")
      .then((response) => response.json())
      .then((data) => setAddress(data));
  }, []);

  // Render the user's information
  const renderUserInfo = () => {
    return (
      <div className="user-info">
        <h1 style={{ fontSize: "24px", margin: 0 }}>User Profile</h1>
        <div className="address">
          <h2 style={{ fontSize: "18px", margin: 0 }}>Address</h2>
          {/* Render user address information here */}
        </div>
      </div>
    );
  };

  return (
    <div
      className="user-profile"
      style={{
        width: "100%",
        margin: "10px",
        marginRight: "5px",
        paddingLeft: "20px",
        border: "1px solid #ccc",
        borderRadius: "5px",
        boxShadow: "0px 0px 5px rgba(0, 0, 0, 0.25)",
      }}
    >
      {posts.length > 0 && address ? (
        <div>
          {renderUserInfo()}
        </div>
      ) : (
        <div>Loading...</div>
      )}

      <div className="user-posts-container" style={{ border: "1px solid #ccc", borderRadius: "5px", margin: "10px", padding: "10px" }}>
        <UserPosts posts={posts} />
      </div>
    </div>
  );
};

export default UserProfilePage;
