import React, { useEffect, useState } from "react";
import UserPosts from "./UserPost";

const UserProfilePage = ({ match }) => {
  const [posts, setPosts] = useState([]);
  const [address, setAddress] = useState(null);

  useEffect(() => {
    fetch("https://jsonplaceholder.typicode.com/posts")
      .then((response) => response.json())
      .then((data) => setPosts(data));

    fetch("https://jsonplaceholder.typicode.com/users/1/address")
      .then((response) => response.json())
      .then((data) => setAddress(data));
  }, []);

  const renderUserInfo = () => {
    return (
      <div className="user-profile">
        <div className="user-info" style={{ float: "left", width: "50%" }}>
          <h1 style={{ fontSize: "24px", margin: 0 }}>User Profile</h1>
        </div>
        <div className="address" style={{ float: "right", width: "100%" }}>
          <h2 style={{ fontSize: "18px", margin: 0 }}>Address</h2>
        </div>
        <div style={{ clear: "both" }}></div>
      </div>
    );
  };

  const goBack = () => {
    window.location.href = "/"; // Replace with the URL you want to navigate back to
  };

  return (
    <div>
      <button onClick={goBack} style={{ float: "left" }}>
        Back
      </button>
      {posts.length > 0 && address ? (
        <div>{renderUserInfo()}</div>
      ) : (
        <div>Loading...</div>
      )}

      <div
        className="user-posts-container"
        style={{
          border: "1px solid #ccc",
          borderRadius: "5px",
          margin: "10px",
          padding: "10px",
          clear: "both",
        }}
      >
        <UserPosts posts={posts} />
      </div>
    </div>
  );
};

export default UserProfilePage;
