import React, { useRef, useEffect } from "react";

const PostDetailsModal = ({ post, isOpen, onClose }) => {
  if (!isOpen || !post) {
    return null;
  }

  const overlayStyle = {
    position: "fixed",
    top: 0,
    left: 0,
    width: "100%",
    height: "100%",
    backgroundColor: "rgba(0, 0, 0, 0.5)",
    zIndex: 1,
    display: "flex",
    justifyContent: "center",
    alignItems: "center",
  };

  const modalContentStyle = {
    backgroundColor: "white",
    padding: "20px",
    borderRadius: "5px",
    boxShadow: "0 4px 8px 0 rgba(0, 0, 0, 0.2)",
    width: "60%",
    maxWidth: "600px",
    textAlign: "center",
    position: "relative",
  };

  // Create a ref for the overlay element
  const overlayRef = useRef(null);

  // Close the modal when clicking outside of it
  const handleCloseOnOutsideClick = (event) => {
    if (overlayRef.current && !overlayRef.current.contains(event.target)) {
      onClose();
    }
  };

  useEffect(() => {
    if (isOpen) {
      // Add the event listener when the modal is open
      document.addEventListener("mousedown", handleCloseOnOutsideClick);
    } else {
      // Remove the event listener when the modal is closed
      document.removeEventListener("mousedown", handleCloseOnOutsideClick);
    }

    return () => {
      // Cleanup the event listener on component unmount
      document.removeEventListener("mousedown", handleCloseOnOutsideClick);
    };
  }, [isOpen]);

  return (
    <div ref={overlayRef} style={overlayStyle} className={`post-details-modal ${isOpen ? "open" : ""}`}>
      <div style={modalContentStyle}>
        <h4>Post Title:</h4>
        <p>{post.title}</p>
        <h4>Content:</h4>
        <p>{post.body}</p>
        <button onClick={onClose}>Close</button>
      </div>
    </div>
  );
};

export default PostDetailsModal;
