import React, { useState, useEffect } from "react";

// UserPosts component
const UserPosts = ({ posts }) => {
  const [currentPost, setCurrentPost] = useState(null);

  // Function to handle when a post is clicked
  const handlePostClick = (post) => {
    setCurrentPost(post);
  };

  // Function to close the modal
  const handleCloseModal = () => {
    setCurrentPost(null);
  };

  // Render the list of post cards
  return (
    <div className="user-posts">
      <h3>Posts</h3>
      {posts.map(post => (
        <div className="post-card" key={post.id} onClick={() => handlePostClick(post)}>
          <h4>{post.title}</h4>
          <p>{post.body}</p>
          <button className="view-post">View Post</button>
        </div>
      ))}
    </div>
  );
};
// UserProfilePage component
const UserProfilePage = ({ match }) => {
  const [posts, setPosts] = useState([]);
  const [address, setAddress] = useState(null);

  // Fetch the posts from the API when the component is first mounted
  useEffect(() => {
    fetch("https://jsonplaceholder.typicode.com/posts")
      .then(response => response.json())
      .then(data => setPosts(data));

    // Fetch the user's address from the API
    fetch("https://jsonplaceholder.typicode.com/users/1/address")
      .then(response => response.json())
      .then(data => setAddress(data));
  }, []);

  // Render the user's information
  const renderUserInfo = () => {
    return (
      <div className="user-info">
        <h1>User Profile</h1>
        <div className="address">
          <h2>Address</h2>
          <p>{address?.street}</p>
          <p>{address?.suite}</p>
          <p>{address?.city}, {address?.zipcode}</p>
        </div>
      </div>
    );
  };

  // Render the UserPosts component with the posts as props
  return (
    <div className="user-profile">
      {posts.length > 0 && address ? (
        <div>
          {renderUserInfo()}
          <UserPosts posts={posts} />
        </div>
      ) : (
        <div>Loading...</div>
      )}
    </div>
  );
};

export default UserProfilePage;
