import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Switch, Route, Link, useParams, useHistory } from 'react-router-dom';
import axios from 'axios';

function UserCard({ user }) {
  return (
    <Link to={`/profile/${user.id}`}>
      <div className="user-card">
        <p>{user.name}</p>
        <p>Posts: {user.posts ? user.posts.length : 0}</p>
      </div>
    </Link>
  );
}


function UserProfile() {
  const { id } = useParams();
  const history = useHistory();
  const [user, setUser] = useState({ posts: [] });

  useEffect(() => {
    // Fetch user details and posts based on the user ID.
    axios.get(`https://jsonplaceholder.typicode.com/users/${id}`)
      .then(response => {
        setUser(response.data);
        return axios.get(`https://jsonplaceholder.typicode.com/posts?userId=${id}`);
      })
      .then(response => {
        setUser(prevUser => ({ ...prevUser, posts: response.data }));
      })
      .catch(error => {
        console.error("Error fetching user data", error);
      });
  }, [id]);

  const goBack = () => {
    history.goBack();
  }

  return (
    <div className="user-profile">
      <button onClick={goBack}>Back</button>
      <h1>{user.name}</h1>
      <p>Username: {user.username}</p>
      <p>Catchphrase: {user.company.catchPhrase}</p>
      <div className="user-address">
        <p>{user.address.street}, {user.address.city}</p>
        <p>{user.address.email}</p>
        <p>{user.phone}</p>
      </div>
      <h2>Posts:</h2>
      <div className="user-posts">
        {user.posts.map(post => (
          <div key={post.id} className="post-card">
            <h3>{post.title}</h3>
            <p>{post.body}</p>
          </div>
        ))}
      </div>
    </div>
  );
}

function App() {
  const [users, setUsers] = useState([]);

  useEffect(() => {
    // Fetch the user list from the API
    axios.get('https://jsonplaceholder.typicode.com/users')
      .then(response => {
        setUsers(response.data);
      })
      .catch(error => {
        console.error("Error fetching user list", error);
      });
  }, []);

  return (
    <div className="App">
      <Router>
        <Switch>
          <Route exact path="/">
            <h1>User Directory</h1>
            {users.map(user => (
              <UserCard key={user.id} user={user} />
            ))}
          </Route>
          <Route path="/profile/:id">
            <UserProfile />
          </Route>
        </Switch>
      </Router>
    </div>
  );
}

export default App;
