import React, { useEffect, useState } from "react";

// UserPosts component
const UserPosts = ({ posts }) => {
  const [currentPost, setCurrentPost] = useState(null);

  // Function to handle when a post is clicked
  const handlePostClick = (post) => {
    setCurrentPost(post);
  };

  // Function to close the modal
  const handleCloseModal = () => {
    setCurrentPost(null);
  };

  // Render the list of post cards
  return (
    <div className="user-posts">
      <h3 style={{ fontSize: "18px", marginBottom: "5px" }}>Posts</h3>
      {posts.map((post) => (
        <div key={post.id} className="post-card">
          <h4 style={{ fontSize: "16px", marginBottom: "5px" }}>{post.title}</h4>
          <p style={{ fontSize: "14px", marginBottom: "5px" }}>{post.body}</p>
          <button
            style={{
              float: "right",
              cursor: "pointer",
              background: "#000",
              color: "#fff",
              padding: "5px 10px",
              borderRadius: "5px",
            }}
            onClick={() => handlePostClick(post)}
          >
            View Post
          </button>
        </div>
      ))}
    </div>
  );
};
