import React, { useState, useEffect } from 'react';

const countries = [];

const setCountries = (data) => {
  countries.push(...data);
};

const CountryClockSelector = ({ clock, onCountryChange, onPauseStartClick }) => {
  const [currentTime, setClock] = useState(new Date());

  useEffect(() => {
    // Start the clock
    const interval = setInterval(() => {
      setClock(new Date());
    }, 1000);

    return () => clearInterval(interval);
  }, []);

  useEffect(() => {
    fetch('http://worldtimeapi.org/api/timezone')
      .then(response => response.json())
      .then(data => setCountries(data));
  }, []);

  const handleCountryChange = (event) => {
    // Get the selected country
    const country = event.target.value;

    // Update the clock
    fetch(`http://worldtimeapi.org/api/timezone/${country}`)
      .then(response => response.json())
      .then(data => {
        const time = data.datetime;
        const date = new Date(time);
        setClock(date);
      });
  };

  const handlePauseStartClick = () => {
    // Pause or start the clock
    setClock(new Date(currentTime.getTime() + (currentTime.isPaused ? 1000 : -1000)));
  };

  return (
    <div className="country-clock">
      <select id="country-selector" onChange={handleCountryChange}>
        {countries.map(country => (
          <option key={country.abbreviation} value={country.abbreviation}>{country.name}</option>
        ))}
      </select>
      <div id="clock">{currentTime.toLocaleTimeString()}</div>
      <button id="pause-start" onClick={onPauseStartClick}>{currentTime.isPaused ? 'Start' : 'Pause'}</button>
    </div>
  );
};

export default CountryClockSelector;
