import React, { useEffect, useState } from "react";
import UserPosts from "./UserPost";
import { useMatch } from "react-router-dom";

const UserProfilePage = ({ user }) => {
  const [posts, setPosts] = useState([]);
  const [userInfo, setUserInfo] = useState(null);
  const [address, setAddress] = useState(null);

  const match = useMatch("/users/:id");
  const { id } = match?.params;

  useEffect(() => {
    // Fetch user-specific data
    fetch(`https://jsonplaceholder.typicode.com/users/${id}`)
      .then((response) => response.json())
      .then((data) => setUserInfo(data));

    // Fetch user's posts
    fetch(`https://jsonplaceholder.typicode.com/posts?userId=${id}`)
      .then((response) => response.json())
      .then((data) => setPosts(data));

    // Fetch user's address (if needed)
    fetch(`https://jsonplaceholder.typicode.com/users/${id}/address`)
      .then((response) => response.json())
      .then((data) => setAddress(data));
  }, [id]);

  const renderUserInfo = () => {
    if (userInfo) {
      const { username, name, email, phone } = userInfo;

      return (
        <div className="user-profile">
          <div className="user-info" style={{ float: "left", width: "50%" }}>
            <h1 style={{ fontSize: "24px", margin: 0 }}>User Profile</h1>
            <p>Username: {username}</p>
            <p>Name: {name}</p>
            <p>Email: {email}</p>
            <p>Phone: {phone}</p>
          </div>
          <div className="address" style={{ float: "right", width: "100%" }}>
            {address && (
              <div>
                <h2 style={{ fontSize: "18px", margin: 0 }}>Address</h2>
                <p>Street: {address.street}</p>
                <p>Suite: {address.suite}</p>
                <p>City: {address.city}</p>
                <p>Zipcode: {address.zipcode}</p>
              </div>
            )}
          </div>
          <div style={{ clear: "both" }}></div>
        </div>
      );
    } else {
      return <div>Loading user information...</div>;
    }
  };

  const goBack = () => {
    window.location.href = "/"; // Replace with the URL you want to navigate back to
  };

  return (
    <div>
      <button onClick={goBack} style={{ float: "left" }}>
        Back
      </button>
      <div>
        <h2 style={{ marginLeft: "40%" }}>Country Dropdown</h2>
      </div>
      <div>
        <h3 style={{ marginLeft: "45%" }}>User Profile</h3>
      </div>
      {posts.length > 0 ? <div>{renderUserInfo()}</div> : <div>Loading...</div>}
    </div>
  );
};

export default UserProfilePage;
