import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Switch, Route, Link, useParams, useHistory } from 'react-router-dom';
import axios from 'axios';

function UserCard({ user }) {
  return (
    <Link to={`/user/${user.id}`}>
      <div className="user-card">
        <p>{user.name}</p>
        <p>Posts: {user.posts ? user.posts.length : 0}</p>
      </div>
    </Link>
  );
}

function UserDetails({ user, posts }) {
  const [currentTime, setCurrentTime] = useState(null);
  const [clockInterval, setClockInterval] = useState(null);
  const [isClockRunning, setIsClockRunning] = useState(true);

  useEffect(() => {
    const startClock = () => {
      const intervalId = setInterval(() => {
        if (isClockRunning) {
          axios.get('http://worldtimeapi.org/api/timezone/Etc/UTC')
            .then(response => {
              setCurrentTime(response.data.utc_datetime);
            })
            .catch(error => {
              console.error('Error fetching current time: ', error);
            });
        }
      }, 1000);
      setClockInterval(intervalId);
    };

    startClock();

    return () => {
      clearInterval(clockInterval);
    }
  }, [isClockRunning]);

  const toggleClock = () => {
    setIsClockRunning(prevIsClockRunning => !prevIsClockRunning);
  };

  return (
    <div className="user-details">
      <div className="user-details-header">
        <h2 className="user-details-title">User Details</h2>
      </div>
      <div className="clock">
        <h3>Current Time</h3>
        <p>{currentTime}</p>
        <button onClick={toggleClock}>
          {isClockRunning ? 'Pause Clock' : 'Start Clock'}
        </button>
      </div>
      <div className="user-info">
        <div className="user-info-left">
          <h3>Name: {user.name}</h3>
          <p>Catch Phrase: {user.company.catchPhrase}</p>
        </div>
        <div className="user-info-right">
          <h3>Address: {user.address.city}</h3>
          <p>Email: {user.email}</p>
          <p>Phone: {user.phone}</p>
        </div>
      </div>
      <div className="user-posts">
        <h3>User Posts</h3>
        {posts.map(post => (
          <div key={post.id} className="post-card">
            <h4>{post.title}</h4>
            <p>{post.body}</p>
          </div>
        ))}
      </div>
    </div>
  );
}

function App() {
  const [users, setUsers] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    axios.get('https://jsonplaceholder.typicode.com/users')
      .then(response => {
        setUsers(response.data);
        setLoading(false);
      })
      .catch(error => {
        console.error('Error fetching user data: ', error);
        setLoading(false);
      });
  }, []);

  return (
    <Router>
      <div className="App">
        <Switch>
          <Route
            exact
            path="/"
            render={() => (
              <div>
                <h1>User Directory</h1>
                {users.map(user => (
                  <Link key={user.id} to={`/user/${user.id}`}>
                    <UserCard user={user} />
                  </Link>
                ))}
              </div>
            )}
          />
          <Route
            path="/user/:id"
            render={({ match }) => {
              const userId = match.params.id;
              const user = users.find(user => user.id === parseInt(userId));
              if (user) {
                return <UserDetails user={user} />;
              } else {
                return <div>User not found</div>;
              }
            }}
          />
        </Switch>
      </div>
    </Router>
  );
}

export default App;
